import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../../services/api';
import './Cars.css';

const CarForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [tags, setTags] = useState('');
  const [images, setImages] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    if (id) {
      fetchCar();
    }
  }, [id]);

  const fetchCar = async () => {
    try {
      const response = await api.get(`/cars/${id}`);
      const car = response.data;
      setTitle(car.title);
      setDescription(car.description);
      setTags(car.tags.join(', ')); // If your tags are in an array, convert them to a string.
    } catch (error) {
      console.error('Error fetching car:', error);
    }
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Create a plain object instead of FormData
    const payload = {
      title,
      description,
      tags: tags.split(',').map((tag) => tag.trim()), // Convert tags to an array
    };

    // If there are images, add them as metadata
    if (images.length > 0) {
      // Instead of files, we just add metadata (file names or types for example)
      payload.images = images.map((image) => ({
        name: image.name,
        type: image.type,
        size: image.size,
      }));
    } else {
      payload.images = []; // Initialize as an empty array if no images are present
    }

    // Log the payload object for debugging
    console.log('Payload before submission:', payload);

    try {
      let response;
      console.log('ID:', id); 

      // Sending as JSON; no need for 'Content-Type' as Axios auto-sets it to 'application/json'
      if (id && id.trim()) {
        response = await api.patch(`/cars/${id}`, payload);
      } else {
        response = await api.post('/cars', payload);
      }

      console.log('Server response:', response.data);
      navigate('/');
    } catch (error) {
      console.error('Error saving car:', error.response ? error.response.data : error.message);
    } finally {
      setIsSubmitting(false);
    }
  };


  return (
    <div className="container">
      <div className="card">
        <h2 className="title">{id ? 'Edit Car' : 'Add New Car'}</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              id="title"
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="tags">Tags (comma-separated)</label>
            <input
              id="tags"
              type="text"
              value={tags}
              onChange={(e) => setTags(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="images">Images</label>
            <input
              id="images"
              type="file"
              multiple
              onChange={(e) => setImages(Array.from(e.target.files))}
            />
          </div>
          <button
            type="submit"
            disabled={isSubmitting}
            className="button"
            style={{ width: '100%' }}
          >
            {isSubmitting ? 'Submitting...' : (id ? 'Update' : 'Create')}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CarForm;
