import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../../services/api';
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "@/components/ui/card"

const CarDetail = () => {
  const [car, setCar] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetchCar();
  }, [id]);

  const fetchCar = async () => {
    try {
      const response = await api.get(`/cars/${id}`);
      setCar(response.data);
    } catch (error) {
      console.error('Error fetching car:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await api.delete(`/cars/${id}`);
      navigate('/');
    } catch (error) {
      console.error('Error deleting car:', error);
    }
  };

  if (!car) return <div className="container">Loading...</div>;

  return (
    <Card className="w-full max-w-3xl mx-auto">
      <CardHeader>
        <CardTitle>{car.title}</CardTitle>
      </CardHeader>
      <CardContent>
        <p className="mb-4">{car.description}</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {car.images.map((image, index) => (
            <img key={index} src={image.url} alt={`Car ${index + 1}`} className="w-full h-auto rounded-lg" />
          ))}
        </div>
        <p className="mt-4">Tags: {car.tags.join(', ')}</p>
      </CardContent>
      <CardFooter className="flex justify-end space-x-2">
        <Button onClick={() => navigate(`/cars/${id}/edit`)}>Edit</Button>
        <Button variant="destructive" onClick={handleDelete}>Delete</Button>
      </CardFooter>
    </Card>
  );
};

export default CarDetail;
