import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { initiateSignup, verifyOTP } from '../../services/auth';

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [error, setError] = useState('');
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleInitiateSignup = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await initiateSignup(username, email, password);
      setIsOtpSent(true);
    } catch (error) {
      console.error('Signup initiation failed:', error);
      setError('Failed to initiate signup. Please try again.');
    }
  };

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await verifyOTP(email, otp);
      login(response.data);
      navigate('/');
    } catch (error) {
      console.error('OTP verification failed:', error);
      setError('Invalid OTP. Please try again.');
    }
  };

  return (
    <div className="auth-container">
      {!isOtpSent ? (
        <form onSubmit={handleInitiateSignup} className="auth-form">
          <h2 className="auth-title">Sign Up</h2>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="auth-input"
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="auth-input"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="auth-input"
            required
          />
          <button type="submit" className="auth-button">Send OTP</button>
        </form>
      ) : (
        <form onSubmit={handleVerifyOTP} className="auth-form">
          <h2 className="auth-title">Verify OTP</h2>
          <p className="auth-message">Please enter the OTP sent to your email.</p>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            className="auth-input otp-input"
            maxLength={6}
            required
          />
          <button type="submit" className="auth-button">Verify OTP</button>
        </form>
      )}
      {error && <p className="auth-message" style={{color: 'red'}}>{error}</p>}
    </div>
  );
};

export default Signup;
