import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../../services/api';

const CarDetail = () => {
  const [car, setCar] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetchCar();
  }, [id]);

  const fetchCar = async () => {
    try {
      const response = await api.get(`/cars/${id}`);
      setCar(response.data);
    } catch (error) {
      console.error('Error fetching car:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await api.delete(`/cars/${id}`);
      navigate('/');
    } catch (error) {
      console.error('Error deleting car:', error);
    }
  };

  if (!car) return <div className="container">Loading...</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-2xl font-bold mb-4">{car.title}</h2>
        <p className="mb-4">{car.description}</p>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4 mb-4">
          {car.images.map((image, index) => (
            <div key={index} className="aspect-w-1 aspect-h-1 w-full overflow-hidden rounded-lg">
              <img 
                src={image.url} 
                alt={`Car ${index + 1}`} 
                className="w-full h-full object-cover object-center"
              />
            </div>
          ))}
        </div>
        <p className="mb-4">Tags: {car.tags.join(', ')}</p>
        <div className="flex justify-end space-x-2">
          <button 
            onClick={() => navigate(`/cars/${id}/edit`)}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Edit
          </button>
          <button 
            onClick={handleDelete}
            className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default CarDetail;
